mapper.select:
select month(created_time) as taskMonth,count(*) as taskCount from s_service_task where 1 = 1
and task_status = #{status}
and year(created_time) = #{year}
and CHARINDEX(#{troubleType},trouble_type)>0
and CHARINDEX(#{assemblyType},assembly_type)>0
group by month(created_time)

select t.user_name,t.dept_name,t.userState,t.address (select suser.user_name,suser.dept_name,(case when task.task_status = 2 then 0 else 1 end) as userState, punch.address from s_user suser left join s_service_task task on suser.uuid = task.executor left join s_task_punch punch on task.uuid = punch.task_id where suser.status = 0 and suser.user_type = 1 and punch.punch_type = 0
and CHARINDEX(#{suser.user_name},suser.user_name) > 0
) t
and t.userState = #{userState}

select month(t.created_time) as taskMonth,AVG(meanTime) as meanTime from (select task.uuid,task.device_type,task.assembly_type,task.trouble_type, audit.created_time,datediff(HOUR,task.created_time,audit.created_time) as meanTime FROM s_service_task task left join s_audit_task audit on task.uuid = audit.task_id and audit.operation = 3 where task.task_status = 2
and year(audit.created_time) = #{year}
and CHARINDEX(#{task.troubleType},trouble_type)>0
and CHARINDEX(#{task.assemblyType},assembly_ype)>0
and CHARINDEX(#{task.deviceType},device_type)>0
) t group by month(t.created_time)
